import React from 'react';
import { useState, useEffect, useRef } from "react";
import ItemImage from './ItemImage';

const images = [
  "https://images.unsplash.com/photo-1559666126-84f389727b9a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1356&q=80",
  "https://images.unsplash.com/photo-1557389352-e721da78ad9f?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80",
  "https://images.unsplash.com/photo-1553969420-fb915228af51?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1049&q=80",
  "https://images.unsplash.com/photo-1550596334-7bb40a71b6bc?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80",
  "https://images.unsplash.com/photo-1550640964-4775934de4af?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80"
];

const Carousel = () => {
  const inputEl = useRef(null);

  // slider.scrollLeft = scrollLeft*2;

  const [index, setIndex] = useState(0);

  useEffect(() => {
    // inputEl.current.focus();
    console.log(inputEl.current.scrollLeft);

    const timer = setInterval(() => {
      console.log('aa');
      // setScrollLeft(100);
      // console.log(inputEl.current.getBoundingClientRect().x);
      console.log(inputEl.current.scrollLeft);
      console.log(inputEl.current.getBoundingClientRect().x);
      inputEl.current.getBoundingClientRect().x = 500;
      inputEl.current.scrollLeft = 500;

    }, 5000);
    return () => clearInterval(timer);
  }, [index]);

  return (
    <div className="carousel">
      <div className="carousel-container" ref={inputEl} style={{
        'transform': `translateX(-${property * (100 / properties.length)}%)`
      }}>
        <div className="carousel-list">
          {
            images.map(img => {
              <ItemImage imgSrc={img} />
            })
          }

          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/10004088_1491055334449687_1187165020_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/10817863_856543214397968_517239188_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/10919749_326992714172441_299394464_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/10617007_939025979457209_6938406_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/1168617_1435408473368301_409182770_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/11189836_754178218029431_2102772742_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/10843869_1658219887738208_2033326788_n.jpg" />
          <img className="carousel-item" src="https://www.maggiesadler.com/wp-content/uploads/2015/10/1515054_1379051609022475_1394148610_n.jpg" />
        </div>
      </div>
    </div>


  )
}

export default Carousel
